async function loader(){return fetch("https://opentdb.com/api_category.php").then(e=>e.json()).then(e=>e.trivia_categories)}async function sort(e){return e.sort((e,t)=>{var o=e.name.toLowerCase(),n=t.name.toLowerCase();return o<n?-1:o>n?1:0}),e}async function populateCategorySelector(e){const t=document.getElementById("category");for(const n of e){var o=document.createElement("option");o.text=n.name,o.value=n.id,t.add(o)}}window.addEventListener("load",()=>{loader().then(sort).then(populateCategorySelector)});